package config


var LogFile = "result.log"
// mssql 执行的结构体，主要用于外部拓展功能
var Inlog bool
type MSSQLSHELL struct {
	Host string
	Port int
	UserName string
	PassWord string
	Method int64
	Commandline bool
	Command string
}
type REDISSHELL struct {
	Host string
	Port int
	PassWord string
}
// 输入数据的结构体，用于扫描功能
type SSHSHELL struct {
	Host string
	Port int
	UserName string
	PassWord string
	Command string
	SshKey string
}
type EnterFlag struct {
	ScanTypeMain string
	Thread int64
	ScanHost string
	Method string
	Noping bool
	Ports string
	Command string
	Fragile string
	FragileBool bool
}

type HostData struct {
	HostName string
	TimeOut int64
	Ports int
	SshKey string
	Command string
}

type PortResult struct {
	IP string
	Port []int
}

type WebInfo struct {
	SetProxy string
	TimeOut int64
	SetCookie string
	Fragile bool
	PocName string
	Target string
}

